import React from 'react'
import { View, Text,Image } from 'react-native'
import pet7 from "../../images/pet7.png";
import storyring from "../../images/Storyring.png";
import tw from "twrnc";

export default function OthersStory({style,item,isStoryActive}) {
    const {image,name}=item
    return (
        <View style={tw`${style} items-center`}>
             {isStoryActive && <Image style={tw`w-[72px] h-[72px]`} source={storyring} />}
             <Image style={tw`w-[60px] h-[60px] rounded-full absolute top-[6px] left-[6px]`} source={require("../../images/" + image)} />
             <Text style={tw`text-[11px] leading-[22px]`}>{name}</Text>
        </View>
    )
}
