import React, { useState } from "react";
import { View, Text, Image } from "react-native";
import storyring from "../../images/global/Storyring.png";
import greycircle from "../../images/global/GreyCircle.png";
import srk from "../../images/friends/srk.jpg";
import msdhoni from "../../images/friends/msdhoni.jpg";
import katrinakaif from "../../images/friends/katrina-kaif.jpg";
import viratKohli from "../../images/friends/virat-kohli.jpg";
import dots from "../../icons/3dots.png";
import saved from "../../icons/saved.png";
import liked from "../../icons/liked.png";
import comment from "../../icons/comment.png";
import share from "../../icons/share.png";
import bluetick from "../../icons/bluetick.png";
import tag from "../../icons/tag.png";
import tw from "twrnc";

export default function Post() {
  const [item, setItem] = useState({
    dp: "srk.jpg",
    name: "iamsrk",
    isVerified: true,
    isStoryActive: true,
    isSponsored: true,
    isReel: false,
    reel: "",
    post: ["srk.jpg"],
    caption:
      "Username Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
    likesCount: "24,51,182",
    commentCount: "1605",
    sampleComments: [{ id: 1, name: "nandini.oops", comment: "ðŸ˜ðŸ˜â¤ï¸â€ðŸ”¥" }, {id:2,name:"alokkumax",comment:"her ðŸ’˜ >>>"}],
  });
  const {
    dp,
    name,
    isVerified,
    isStoryActive,
    isSponsored,
    isReel,
    reel,
    post,
    caption,
    likesCount,
    commentCount,
  } = item;
  return (
    <View>
      {/* Post Header */}
      <View
        style={tw`flex-row justify-between items-center px-[13px] py-[7px] `}
      >
        <View style={tw`flex-row`}>
          <View style={tw`mr-[10px] w-[36px]`}>
            <Image
              style={tw`w-[40px] h-[40px]`}
              source={isStoryActive ? storyring : greycircle}
            />
            <Image
              style={tw`w-[34px] h-[34px] rounded-full absolute top-[3px] left-[3px]`}
              source={require("../../images/friends/" + dp)}
            />
          </View>
          <View style={tw`justify-center`}>
            <View style={tw`flex-row mb-[2px]`}>
              <Text style={tw`font-bold text-[12px] mr-[4px]`}>{name}</Text>
              {isVerified && (
                <Image style={tw`w-[13px] h-[13px] `} source={bluetick} />
              )}
            </View>
            {isSponsored && <Text style={tw`text-[10px]`}>Sponsored</Text>}
          </View>
        </View>
        <Image style={tw`w-[24px] h-[24px]`} source={dots} />
      </View>

      {/* Post Picture */}
      <View>
        <Image style={tw`h-[360px]`} source={srk} />
        <Image
          style={tw`w-[24px] h-[24px] rounded-full absolute bottom-[10px] left-[16px]`}
          source={tag}
        />
      </View>

      {/* Post Footer */}
      <View
        style={tw`flex-row justify-between items-center px-[12px] py-[10px] `}
      >
        <View style={tw`flex-row`}>
          <Image style={tw`w-[24px] h-[24px] mr-[20px]`} source={liked} />
          <Image style={tw`w-[24px] h-[24px] mr-[20px]`} source={comment} />
          <Image style={tw`w-[24px] h-[24px]`} source={share} />
        </View>
        <Image style={tw`w-[24px] h-[24px]`} source={saved} />
      </View>

      {/* Post Footer 2 */}
      <View style={tw`px-[12px] `}>
        <View style={tw` d-flex items-center flex-row pb-[5px]`}>
          <Image
            style={tw`w-[24px] h-[24px] rounded-full border-2 border-white z-30`}
            source={msdhoni}
          />
          <Image
            style={tw`w-[24px] h-[24px] rounded-full border-2 border-white absolute left-[16px] z-20`}
            source={katrinakaif}
          />
          <Image
            style={tw`w-[24px] h-[24px] rounded-full border-2 border-white absolute left-[32px] z-10`}
            source={viratKohli}
          />
          <Text style={tw`text-[12px] absolute left-[70px]`}>
            Liked by <span style={tw`font-bold`}>alokkumax</span> and{" "}
            <span style={tw`font-bold`}>{likesCount} others</span>
          </Text>
        </View>
        <View style={tw``}>
          <Text style={tw`text-[12px] mb-[5px]`}>
            <span style={tw`font-bold mr-[4px]`}>{name}</span>
            {caption.length >= 100 ? caption.slice(0, 100) : caption}
            <span style={tw`text-[#6E6E6E]`}>
              {caption.length >= 100 ? "... more" : ""}
            </span>
          </Text>
          <Text style={tw`text-[12px] text-[#6E6E6E]`}>
            View all {commentCount} comments
          </Text>
        </View>
      </View>
    </View>
  );
}
