import React from "react";
import { View, Text, Image } from "react-native";
import storyring from "../../images/Storyring.png";
import greycircle from "../../images/GreyCircle.png";
import tw from "twrnc";

export default function Post({ isStoryActive }) {
  return (
    <View style={tw`px-[13px] py-[7px]`}>
      <View>
        <Image
          style={tw`w-[36px] h-[36px]`}
          source={isStoryActive ? storyring : greycircle}
        />
        <Image
          style={tw`w-[30px] h-[30px] rounded-full absolute top-[3px] left-[3px]`}
          source={require("../../images/pet5.png")}
        />
        <Text style={tw`text-[11px] leading-[22px]`}>Lily</Text>
      </View>
    </View>
  );
}
