import React, { useState } from "react";
import { View, Text, Image } from "react-native";
import storyring from "../../images/Storyring.png";
import greycircle from "../../images/GreyCircle.png";
import dots from "../../icons/3dots.png";
import post1 from "../../images/post1.png";
import tag from "../../icons/tag.png";
import tw from "twrnc";

export default function Post() {
  const [item, setItem] = useState({
    dp: "pet7.png",
    name: "Ruffles",
    isStoryActive: true,
    isSponsored: true,
    isReel: false,
    reel: "",
    post: ["pet6.png"],
  });
  const { dp, name, isStoryActive, isSponsored, isReel, reel, post } = item;
  return (
    <View>
      {/* Post Header */}
      <View style={tw`flex-row justify-between items-center px-[13px] py-[7px] `}>
        <View style={tw`flex-row`}>
          <View style={tw`mr-[8px] w-[36px]`}>
            <Image
              style={tw`w-[40px] h-[40px]`}
              source={isStoryActive ? storyring : greycircle}
            />
            <Image
              style={tw`w-[34px] h-[34px] rounded-full absolute top-[3px] left-[3px]`}
              source={require("../../images/" + dp)}
            />
          </View>
          <View style={tw`justify-center`}>
            <Text style={tw`font-bold text-[12px]`}>{name}</Text>
            {isSponsored && <Text style={tw`text-[11px]`}>Sponsored</Text>}
          </View>
        </View>
        <Image style={tw`w-[24px] h-[24px]`} source={dots} />
      </View>

      {/* Post Picture */}
      <View>
        <Image style={tw`h-[400px]`} source={post1} />
        <Image style={tw`w-[24px] h-[24px] rounded-full absolute bottom-[10px] left-[16px]`} source={tag} />
      </View>
    </View>


  );
}
