import { View, Text } from 'react-native'

export default function Comment() {
    return (
        <View>
            <View style={tw`flex-row justify-between mb-[3px]`}>
                <View style={tw`flex-row`}>
                  <Text style={tw`text-[12px] font-bold mr-[3px]`}>
                    {item.name}
                  </Text>
                  <Text style={tw`text-[12px]`}>{item.comment}</Text>
                </View>
                <View>
                  <TouchableOpacity onPress={()=>{
                    setCommentLike(!isCommentLiked)
                    setCommentLikeIndex(index)
                    }}>
                    <Image
                      style={tw`w-[10px] h-[10px]`}
                      source={isCommentLiked && commentLikeIndex===index ? liked : like}
                    />
                  </TouchableOpacity>
                </View>
              </View>
        </View>
    )
}
