import React from "react";
import { TouchableOpacity } from "react-native";
import { View, Text } from "react-native";
import IconAD from "react-native-vector-icons/AntDesign";
import IconF from "react-native-vector-icons/Feather";
import tw from "twrnc";

export default function DmHeader({ navigation, item }) {
  const { name, username, image } = item;
  return (
    <View
      style={tw`flex-row items-center justify-between pt-[20px] pb-[15px] px-[13px]`}
    >
      <View style={tw`flex-row items-center`}>
        <TouchableOpacity onPress={() => navigation.goBack()}>
          <IconAD name="arrowleft" size={30} style={tw`mr-[30px]`} />
        </TouchableOpacity>
        <View style={tw`flex-row`}>
          <View style={tw`mr-[15px] w-[60px] flex items-center`}>
            {item.isStoryActive && (
              <Image style={tw`w-[60px] h-[60px]`} source={storyring} />
            )}

            <Image
              style={tw`w-[52px] h-[52px] rounded-full ${
                item.isStoryActive ? "absolute top-[4px] left-[4px]" : ""
              } `}
              source={item.image}
            />
          </View>
          <View style={tw`justify-center`}>
            <View style={tw`flex-row mb-[2px]`}>
              <Text
                style={tw`${
                  item.msgsCount ? "text-black" : "text-[#6E6E6E]"
                } font-bold text-[12px] mr-[4px]`}
              >
                {item.name}
              </Text>
              {item.isVerified && (
                <Image style={tw`w-[13px] h-[13px] `} source={bluetick} />
              )}
            </View>
            {item.msgsCount > 0 && (
              <View style={tw`flex-row`}>
                <Text style={tw`font-bold text-[12px] mr-[4px]`}>
                  {item.msgsCount}
                  {" new messages"}
                </Text>
                <Text style={tw`text-[12px] mr-[4px]`}>.</Text>
                <Text style={tw`text-[12px]`}>{item.sentWhen}</Text>
              </View>
            )}
            {item.msgsCount === 0 && (
              <Text style={tw`text-[12px] text-[#6E6E6E]`}>
                {"Seen "}
                {item.seenWhen}
              </Text>
            )}
          </View>
        </View>
      </View>
      <IconF name="edit" size={25} style={tw`mr-[10px]`} />
    </View>
  );
}
