import React from "react";
import { View, Text, FlatList,Image } from "react-native";
import chats from "../../constants/chats";
import tw from "twrnc";

export default function AllDms({item}) {
    const {image} =item;
  return (
    <View style={tw`px-[13px]`}>
      <FlatList
        data={chats}
        keyExtractor={(item,index) => index}
        renderItem={({ item, index }) => (
          <>
          <View style={tw`flex-row items-end flex-1`}>
             <Image style={tw`w-[30px] h-[30px] rounded-full mr-[10px]`} source={image}/>
             <FlatList
                data={item.msgs}
                keyExtractor={(msg,index) => index}
                renderItem={({ item, index }) => (
                <>
                    <View style={tw`max-w-[200px] bg-[#e6e6e6] mb-[2px]`}>
                        <Text>{item}</Text>
                    </View>

                </>
                )}/>

            </View>
          </>
        )}
      />
    </View>
  );
}
